void setup() {
  size(800, 800);
  background(0);
  noStroke();
  R=200;
}
void draw() {
  fill(255.255, 255,50);
  translate(width / 2, height / 2);
  scale(1 + a *  noise(angle * 10));
  a = 1*noise(b);
  for (float R=200; R>10; R-=2) {
    fill(noise(x*y)*R, noise(x/y)*R, noise(x-y)*R);
    x=sin(angle)*(R+a);
    y=cos(angle) * (R+a);
    ellipse(x, y, 2, 2);
  }
  angle += 0.01;
}

