function drawLolipop() {
  myTurtle.pushState();
  myTurtle.penUp();
  for (xpos = 0; xpos < 500; xpos += 40) {
    myTurtle.pushState();

    myTurtle.moveTo(xpos, 0);
    myTurtle.turnRight(45);
    myTurtle.penDown();
    myTurtle.moveForward(40);
    myTurtle.turnRight(60);
    for (i = 0; i < 6; i++) {
      myTurtle.turnRight(60);
      myTurtle.moveForward(5);
    }
    myTurtle.popState();
  }
  myTurtle.popState();
}

function drawLolipop_1() {
  myTurtle.pushState();
  myTurtle.turnTo(270);
  myTurtle.penUp();
  for (xpos = 0; xpos < 500; xpos += 40) {
    myTurtle.pushState();

    myTurtle.moveTo(xpos, 500);
    myTurtle.turnRight(45);
    myTurtle.penDown();
    myTurtle.moveForward(40);
    myTurtle.turnRight(60);
    for (i = 0; i < 6; i++) {
      myTurtle.turnRight(60);
      myTurtle.moveForward(5);
    }
    myTurtle.popState();
  }
  myTurtle.popState();
}

function drawFlowers(flowersX) {
  for (num = 2; num < 9; num++) {
    drawPetals(flowersX, flowersY * num);
  }
}


function drawPetals(x, y) {

  myTurtle.pushState();
  myTurtle.penUp();
  for (i = 0; i < 6; i++) {
    myTurtle.moveTo(x, y);
    myTurtle.penDown();
    myTurtle.turnRight(i * 60);
    myTurtle.moveForward(10);
    myTurtle.turnRight(105);
    myTurtle.moveForward(5);
    myTurtle.turnRight(105);
    myTurtle.moveForward(10);
    myTurtle.turnTo(0);
  }
  myTurtle.popState();

}

function drawSun() {
  myTurtle.pushState();
  myTurtle.penUp();
  myTurtle.moveTo(250, 250);
  // put the pen down to draw
  myTurtle.penDown();
    strokeWeight(1.5);

  for (dir = 0; dir < 360; dir += 20) {
    myTurtle.moveTo(250, 250);
    myTurtle.turnTo(dir);
    myTurtle.penUp();
    myTurtle.moveForward(30);
    myTurtle.penDown();
    drawDash();
  }
  myTurtle.popState();
}

function drawDash() {

  for (i = 0; i < 10; i++) {
    myTurtle.penDown();
    myTurtle.moveForward(3);
    myTurtle.penUp();
    myTurtle.moveForward(5);
  }
}

function drawSun_1() {
  myTurtle.pushState();
  myTurtle.penUp();
  myTurtle.moveTo(250, 250);
  // put the pen down to draw
  myTurtle.penDown();

  for (dir = 0; dir < 360; dir += 20) {
    myTurtle.moveTo(250, 250);
    myTurtle.turnTo(10 + dir);
    myTurtle.penUp();
    myTurtle.moveForward(80);
    myTurtle.penDown();
    drawDash_1();
  }
  myTurtle.popState();
}

function drawDash_1() {

  for (i = 0; i < 5; i++) {
    myTurtle.penDown();
    myTurtle.moveForward(10);
    myTurtle.penUp();
    myTurtle.moveForward(5);
    myTurtle.penDown();
    myTurtle.moveForward(1);
    myTurtle.penUp();
    myTurtle.moveForward(5);
  }
}

