            var xs = [];
            var ys = [];
            var phase = 0;


            function setup() {
                createCanvas(window.innerWidth, window.innerHeight * .5);
                background(250);

                for (var i=1; i<1000; i++) {
                    xs[i] = map(i, 1, 1000, 0, 2*PI);
                    ys[i] = 100*sin(1*xs[i]) + height/2;
                }


                for (var i=1; i<1000; i++) {
                    ellipse(xs[i]*(width/(2*PI)), ys[i], 5);
                }

                createP('Amplitude');
                amp_slider = createSlider(0, 200, 100);

                createP('Periods');
                periods_slider = createSlider(1, 15, 1);

                createP('Slowness');
                speeds_slider = createSlider(16, 128, 64)
            }


            function draw() {
                background(250);
                var amp = amp_slider.value();
                var periods = periods_slider.value();
                var speed = speeds_slider.value();

                phase += PI/speed;
                for (var i=1; i<1000; i++) {
                    xs[i] = map(i, 1, 200, 0, 2*PI);
                    ys[i] = amp*sin(periods*xs[i] + phase) + height/2;
                }

                for (var i=1; i<1000; i++) {
                    ellipse(xs[i]*(width/(2*PI)), ys[i], 5);
                }
            }
