    fill(0);

    // Grid

    var cols = 12
    var rows = 4;

    var col = width / cols;
    var row = height / rows;

    var x = col / 2;
    var y = row / 2;

    for (var i = 1; i < cols; i++) {

    	line(i * col, 0, i * col, height);

    	for (var j = 1; j < rows; j++) {

    		line(0, j * row, width, j * row);

    	}

    };

    // Angles

    var right = 7 * PI / 6.15;
    var left = 3 * PI / 3.45;
    var straight = PI;

    // Draw long rectangle

    function drawLongRect(rotation, xNum, yNum) {

    	push();
    	translate(x * xNum, y * yNum);
		rotate(rotation);
    	rect(0, 0, 22, 66);
    	pop();

    }

    function drawShortRect(top, xNum, yNum) {

    	push();

    	if (top == true) {

    		translate(0, - y + 22);

    	} else {

    		translate(0, y - 22);

    	}

    	rect(x * xNum, y * yNum, 44, 22);

    	pop();

    }

    function drawEllipse(xNum, yNum) {

    	push();
    	translate(x * xNum, y * yNum);
    	ellipse(0, 0, 50, 50);
    	pop();

    }

    // ### C ###
    drawLongRect(right, 1, 1);
    drawLongRect(left, 1, 3);
    drawShortRect(false, 3, 3);
    drawShortRect(true, 3, 1);

    // ### O ###
    drawEllipse(7, 1);

    // ### M ###
    drawLongRect(straight, 11, 1);
    drawLongRect(straight, 11, 3);
    drawLongRect(left, 13, 1);
    drawLongRect(right, 15, 1);
    drawLongRect(straight, 17, 1);
