   this.yspeed = -this.yspeed;
   backgroundColor = 250;
   score++;
  }

 }


 this.move = function() {
  this.update();
  if (this.x <= this.diameter || this.x >= width - this.diameter) {
   this.xspeed = -this.xspeed * 5;
  }

  this.x += this.xspeed;
  this.y += this.yspeed * slowDown / 10;

 };

