        let mySound;
        let fft;

        function preload() {
            mySound = loadSound('01 Circle Of Life.m4a');
        }

        function setup() {
            createCanvas(800, 1000);
            fft = new p5.FFT(.8, 128);
            fft.setInput(mySound);

            startButton = createButton('start');
            startButton.mousePressed(start);

            stopButton = createButton('stop');
            stopButton.mousePressed(stop);
            frameRate(30)
        }

        function start() {
            mySound.loop(0, 1, 1, 1, 240);
        }

        function stop() {
            mySound.pause();
        }

        function draw() {
            noStroke();

            const data = fft.analyze();

            for (let i = 0; i < 128; i++) {
                const y = map(data[i], 0, 255, 0, 255);
                fill(y, i * 4, i * y*2)
                ellipse(millis()/50-40, 500, 1, y * 1000/255);

                if(millis()>=13500) {
                    fill(y, i * 4, i * y*2)
                    ellipse((800-((millis()-13500)/50)), 500, 1, y * 1000/255)
                }

                if(millis()>=42000) {
                    fill(100, y, 100)
                    ellipse(800-3.5*((millis()-42000)/50), 500, 1, (y/20)*(y/20) * 600/255)
                }

                if(millis()>=54000) {
                    fill(255, y, 0)
                    ellipse(6*((millis()-54000)/50), 500, 1, (y/20)*(y/20)*300/255)
                }
            }
        }

