	for (row = 0; row < rows; row++) {
		for (col = 0; col < rows; col++) {
			fill(0);
			stroke(0);
			drawThing(col * 10 + mouseX, row * random(150) + mouseY);
			drawThing(col * 20 + mouseX / 100, row * random(300));
			drawThing(col * 30 + mouseX / 100, row * random(450));
			drawThing(col * 50 + 200 + mouseX / 100, row * random(10) + 200);
			drawThing(col * 50 + mouseX / 100, row * random(10) + 300);
			drawThing(col * 50 + mouseX / 100, row * random(210) + 400);
			drawThing(col * 50, row * random(10) + 500);}
   }
}

function drawThing(x, y) {
	var radius;
	radius = random(3);
	strokeWeight(random(0, 1));
        ellipse(x + 100, y + 100, radius / random(10), radius / 500);
}

