function drawBranch(length) {

    if (length < 3) {
        return;
    }

    // draw this branch
    strokeWeight(length / 50);
    myTurtle.moveForward(length * random(.5, 1));

    // left child
    myTurtle.pushState();
    myTurtle.turnLeft(random(10, 45));
    drawBranch(length * random(.5, 1));

    myTurtle.popState();

    // right child
    myTurtle.pushState();
    myTurtle.turnRight(random(10, 45));
    drawBranch(length * random(.5, 1));

    myTurtle.popState();

}

