var t;

function setup() {
	createCanvas(800, 800);
	noFill();
	t = new Turtle();
	stroke(0, 200, 100);
	noLoop();
	frameRate(60);
	colorMode(HSB, 100, 100, 100, 100);
}

function draw() {
	background(25);
	t.penUp();
	flower();
	bird();


}

function flower() {
	t.pushState();
	translate(-140, -120);
	t.penDown();
	for (i = 0; i < 60; i++) {
		t.turnLeft(map(i, 0, 30, 30, 70));

		t.pushState();
		for (j = 0; j < 120; j++) {
			stroke(sin(i / 60 * PI) * 25 + 45, 65, 100, map(sin(j / 120 * PI), 0, 1, 0, 15));
			strokeWeight(map(sin(j / 120 * PI), 0, 1, 0.2, 1));
			t.turnLeft(sin(j / 120 * PI));
			t.pushState();
			t.moveForward((sin(j / 120 * PI)) * map(i, 0, 30, 70, 120));
			t.popState();
		}
		t.popState();

	}

	stroke(8, 0, 100, 20);
	strokeWeight(3);
	t.turnRight(30);
	t.moveForward(70);
	t.turnRight(10);
	strokeWeight(4);
	stroke(8, 0, 100, 40);
	t.moveForward(40);
	t.turnRight(20);
	strokeWeight(6);
	stroke(8, 0, 100, 60);
	t.moveForward(80);
	t.turnLeft(20);
	strokeWeight(7);
	t.moveForward(120);
	t.popState();
	t.penUp();
}



function petal(iteration, total) {

	t.pushState();
	t.penUp();
	stroke(10, 0, 100, map(iteration, 0, total, 100, 0));
	strokeWeight(map(iteration, 0, total, 0.3, 0.2));
	t.moveForward(noise(iteration * 0.05) * map(iteration, 0, total, 10, 20));
	t.penDown();
	t.moveForward(noise(iteration * 0.01) * map(iteration, 0, total, 100, 200));
