function branch(iteration, branchLength, angle) {
	if (iteration == 0) return;
	stroke(10, iteration * 10 + 20, iteration * 5 + 90, iteration * 12);
	strokeWeight(map(iteration, 0, 10, 0.01, 5));
	t.moveForward(branchLength);
	t.pushState();
	rightend(120, 0.3);
	t.popState();
	t.turnLeft(angle);
	branch(iteration - 1, branchLength / 1.1, angle);
	t.turnRight(angle);
	t.moveBackward(branchLength);
	// //if (iteration>4){
	t.turnRight(angle);
	branch(iteration - 1, branchLength / 1.1, angle);
	t.turnLeft(angle);
	//t.back(branchLength);
	//t.back(branchLength);
	//}
}

function rightend(branchLength, radio) {

	for (j = 0; j < 6; j++) {
		strokeWeight(0.6);
		//t.turnRight(random((1-j),6-j));
		t.turnRight(random(-j, 10 + j));
		t.moveForward(sin((radio + 0.02) * PI) * branchLength * 0.08 + random(-radio, radio));
	}
}

function leftend(branchLength, radio) {

	for (j = 0; j < 6; j++) {
		strokeWeight(0.3);
		//t.turnRight(random((1-j),6-j));
		t.turnLeft(random(-j - 5, 10 + j));
		t.moveForward(sin((radio + 0.02) * PI) * branchLength * 0.04 + random(-radio, radio));
	}
}

