const balls = [];

class Ball {
  constructor() {
    this.x = random(500);
    this.y = random(500);
    this.dX = random(-1, 1);
    this.dY = random(-1, 1);
  }
  step() {
    this.dY += 1;
    this.x += this.dX;
    this.y += this.dY;
    if (this.y > 500) {
      this.dY = -abs(this.dY);
    }
  }
  draw() {
    fill(255);
    noStroke();
    ellipse(this.x, this.y, 30, 30);
  }

}

function setup() {
  createCanvas(500, 500);
  background(0, 50, 0);
  for (let i = 0; i < 10; i++) {
    balls.push(new Ball());
  }

}

function draw() {
  background(50);
  balls.forEach((ball) => {
    ball.step();
    ball.draw();
  });
}

