function drawCircle(i, px, py) {

    if (b[i] == false) {
        fill(255);
        ellipse(width / 2 +px, height / 2+py, (frameCount + i) % sizeC, (frameCount + i) % sizeC);

    } else {
        fill(0);
        ellipse(width / 2+px, height / 2+py, (frameCount + i) % sizeC, (frameCount + i) % sizeC);

    }

    if ((frameCount + i) % 60 == 0) {
        b[i] = !b[i];
    }
}

