var connections = new Group();
function generateConnections(paths) {
	connections.children = [];
	for (var i = 0, l = paths.length; i < l; i++) {
		for (var j = i-1 ; j >= 0; j--) {
		    var path = metaball(paths[j], paths[i], 0.2, handle_len_rate, 40,j);
			if (path) {
				connections.appendTop(path);
				path.removeOnMove();
			}
		}
