// require /turtles/turtle/turtle.js

var myTurtle;

function setup() {
	createCanvas(500, 500);
	noFill();
    colorMode(HSB,360);
    strokeWeight(0.1);
	background(0);
	noLoop();
	myTurtle = new Turtle();
}



function draw() {

	myTurtle.penUp();
	myTurtle.moveTo(50, 450);
	myTurtle.turnTo(-45);
	myTurtle.penDown();

    stroke(200,255,255);

	drawBranch(28);

	drawBranch(40);

	drawBranch(55);

	drawBranch(75);

	drawBranch(100);
}


function drawBranch(length) {
    var a = 200;

	if (length < 6) {
		return;
	}

	myTurtle.moveForward(length+max(random(0,20),random(0,20)));

	// left child
	myTurtle.pushState();
	myTurtle.turnLeft(a*length);
	drawBranch(length * 0.75);

	myTurtle.popState();

	// right child
	myTurtle.pushState();
	myTurtle.turnRight(a/length);
	drawBranch(length * 0.75);

	myTurtle.popState();

	// middle child
	myTurtle.pushState();
	myTurtle.turnLeft(a*length);
	drawBranch(length * 0.75);

	myTurtle.popState();

}
