var testImage;

function preload() {
    testImage = loadImage("/base1.png");
    noLoop();
}

function setup() {
    // create a place to draw
    createCanvas(testImage.width,testImage.height);
  rectMode(CENTER);
  ellipseMode(CENTER);

    // load up the pixel[] array so we can read colors out of it later
    testImage.loadPixels();
}


function draw() {
    // clear the background
    background(0, 0, 0);

    // set drawing styles
    stroke(255, 0, 0);
    fill(255, 255, 255);


    var start = millis();

    // loop over every x,y pixel coordinate in the image
    for (x = 0; x < testImage.width; x++) {
        for (y = 0; y < testImage.height; y++) {

            // slow
            // this is _really_ slow, it might crash your browser
            // var pixelRed = red(testImage.get(x, y));

            // quick
            var pixelRed = getQuick(testImage, x, y)[0];
           var pixelGreen = getQuick(testImage, x, y)[1];
            var pixelBlue = getQuick(testImage, x, y)[2];


            // pick a random value and compare it pixelRed
            // for example:
            // if pixelRed is 0, we'll never draw
            // if pixelRed is 255, we'll always draw
            // if pixelRed is 127, we'll draw 50% of the time
            if (random(255) > pixelRed) {

            }else if (random(255) > pixelBlue){
                stroke( 255, 10);
                // noStroke();
                drawShadow(x, y);

            }else if (random(255) > pixelGreen){
                fill(0, 100, 200, 80);
               noStroke();
                drawDot(x, y);
            }else{
              fill(pixelRed,pixelGreen,pixelBlue);
                noStroke();
               drawDot(x, y);
            }
        }
    }

    var end = millis();

    console.log(`took ${floor(end - start)} ms`);

    noLoop();
}

