function recordParticles() {
    p.push(new Particle(width/2, height/2));        
}


function drawParticles() {
    p.forEach(function(i) {
        let force = createVector(random(-0.7,0.7), random(-0.5,0.5));
        force=force.normalize();
        force.mult(globalSpeed)
        i.applyForce(force);

      //console.log(force);
        i.update();
        i.show();
    });
  // print(p.length)
}


function Particle(x, y) {
    this.x = x;
    this.y = y;
    this.r = 5;
    this.maxR = random(6, 22);
    this.blue = random(80,210);
    this.green = random(40,190);
    this.red = random(100,255);
    this.switch = false;
    this.pos = createVector(this.x, this.y);
    this.vel = createVector(random(-5, 5), random(-5, 5));
    this.acc = createVector();


    this.show = function() {
        noStroke();
        fill(this.red, this.green, this.blue);
        ellipse(this.pos.x, this.pos.y, this.r*1.6);
    }



    this.update = function() {
        // this.acc.mult(globalSpeed)

        this.vel.add(this.acc);
        this.pos.add(this.vel);
        this.acc.mult(0.001);
        // this.vel.mult(globalSpeed*0.00001);
        if (!this.switch) {
            if (this.r < this.maxR) {
                this.r += 1;
            } else {
                this.switch = true;
            }
        } else {
            if (this.r > 0) {
                this.r -= 0.4;
            } else {
                this.r = 3;
            }
        }
        if (this.pos.x>width-this.r || 
          this.pos.x<this.r || 
          this.pos.y>height-this.r ||
          this.pos.y<this.r) {
          // print(p)
          p.splice(p.indexOf(this),1)
        }
    }


    this.applyForce = function(f) {
        this.acc.add(f);
    }
}

