var sourceText = "Comp Form";
var posY;
var mono;

function preload() {
  mono = loadFont('assets/IBMPlexMono-Medium.ttf');

}
function setup() {
  createCanvas(500, 500);
  noStroke();
  fill(200);
  frameRate(60);
  posY = 180;

}



function draw() {
  clear();
  background('#F0F6EC');

  let alpha = map(frameCount, 150, 200, 255, 0);
  fill(0, 0, 0, alpha);

  if (frameCount > 30) {
    tri1 = triangle(500 / 2, 150, 500 / 2 + 30, 200, 500 / 2 - 30, 200);
  }
  if (frameCount > 60) {
    tri2 = triangle(250 - 30, 200, 220 + 30, 250, 220 - 30, 250);
  }
  if (frameCount > 90) {
    tri3 = triangle(280, 200, 280 + 15, 225, 280 - 15, 225);
  }
  if (frameCount > 120) {
    tri4 = triangle(265, 225, 265 + 15, 250, 265 - 15, 250);
  }
  if (frameCount > 150) {
    tri5 = triangle(295, 225, 295 + 15, 250, 295 - 15, 250);
  }

  fill(0, 0, 0, alpha);
  textSize(50);
  textAlign(CENTER, CENTER);
  textFont(mono);

  if (frameCount < 80) {
    text(sourceText, 250, posY += 1.5);
  } else if (frameCount >= 80) {
    posY = 300;
    text(sourceText, 250, posY);
  }
  console.log(frameCount, posY);


}

