// require https://cdn.jsdelivr.net/npm/p5@0.10.2/lib/p5.js

var unit = 9;
var increment = 9;
var columns, rows;
var zoff=0;

function setup() {
  createCanvas(600, 600);
  columns = floor(width/unit);
  rows = floor(height/unit);

}

function draw() {
  background(0);
  loadPixels();
  var xoff=0;
  for (var x=0; x<columns; x++){
    var yoff = 0;
    for(var y=0; y<rows; y++){
      var a = noise(xoff,yoff,zoff)*TWO_PI;
      var vec = p5.Vector.fromAngle(a);
      yoff+= increment;
      stroke(255);
      strokeWeight(0.15)
      push();
      translate(x*unit,y*unit);
      rotate(vec.heading());
      line(0,0,unit,0);

     ellipse(unit*5,unit*7,noise(xoff+yoff)*3);
      pop();
    }
    xoff+=increment;
    zoff +=0.0003;
  }
}

