//require https://cdn.jsdelivr.net/npm/p5@0.10.2/lib/p5.js

function makeCells(cols, rows) {
    var arr = new Array(cols);
    for (i = 0; i < arr.length; i++) {
        arr[i] = new Array(rows);
    }
    return arr;
}

var grid;
var cols;
var rows;
var resolution = 50;

function setup() {
    createCanvas(550, 550);

    cols = width / resolution;
    rows = height / resolution;

    grid = makeCells(cols, rows);
    for (i = 0; i < cols; i++) {
        for (j = 0; j < rows; j++) {
            grid[i][j] = int(random(6));
        }
    }

    noStroke()
}

function draw() {
    background(0);
    for (i = 0; i < cols; i = i+1) {
        for (j = 0; j < rows; j = j+1) {
            var x = resolution * i;
            var y = resolution * j;
            if(grid[i][j] == 1){
                fill(255);
                ellipse(x+resolution/2+5, y+resolution/2+2.5, resolution-5, resolution-5);
            }else if(grid[i][j] == 2){
                fill(200);
                triangle(x+5, y+resolution, x+resolution/2, y+5, x+resolution, y+resolution)
            }else if(grid[i][j] == 3){
                fill(150)
                rect(x+5, y+5, (resolution-5)/2, resolution-5);
                ellipse(x+resolution/2+5, y+resolution/2+2.5, resolution-5, resolution-5);
            }
        }
    }
}

