var myTurtle;

function setup(){
  createCanvas(500, 500);
  noFill();
  stroke(255);
  //background(50);
  noLoop();
  myTurtle = new Turtle();
}

function draw(){
  background(50);
  myTurtle.penUp();
  myTurtle.moveTo(250, 450);
  myTurtle.turnTo(-70);
  myTurtle.penDown();
  drawBranch(100);
}

function drawBranch(length){
  if(length < 10){

    for (var i = 0; i < 3; i++) {
    stroke(0, random(0, 200), 0, random(100, 255));
		myTurtle.moveForward(10);
		myTurtle.turnRight(120);
	}
    return;
  }

  strokeWeight(length / 10);
  stroke(171, 122, 70,random(100, 255));
  myTurtle.moveForward(length * .5);
  //myTurtle.turnLeft(10);
  myTurtle.turnLeft(random(10, 20));
  myTurtle.moveForward(length * .5);

  myTurtle.pushState();
  myTurtle.turnLeft(random(20, 40));
  drawBranch(length * 0.75);
  myTurtle.popState();


  //
  myTurtle.pushState();
