let isPressed;

function setup() {
    createCanvas(600, 600);

    ball = createSprite(width/2, 400, 20, 20);
    hoop = createSprite(550, 200, 100, 100);

    presentPos = [ball.position.x, ball.position.y];

    ball.onMousePressed = function() {
        isPressed = true;
    }

    boxes = new Group();
    var box = createSprite(5, height * 0.5, 10, height);
    box.immovable = true;
    boxes.add(box);
    var box2 = createSprite(width - 5, height * 0.5, 10, height);
    box2.immovable = true;
    boxes.add(box2);
    var box3 = createSprite(width * 0.5, 5, width, 10);
    box3.immovable = true;
    boxes.add(box3);
    var box4 = createSprite(width * 0.5, height - 5, width, 10);
    box4.immovable = true;
    boxes.add(box4);
}

function reduceVelocity() {
    ball.velocity.y*=.5;
}

function draw() {
    background(50, 50, 80);
    previousPos = presentPos;
    presentPos = [ball.position.x, ball.position.y];
    ball.bounce(boxes, reduceVelocity);

    if (isPressed) {
        ball.position.x = mouseX;
        ball.position.y = mouseY;
    }
    if (mouseWentUp(RIGHT) || mouseWentUp(LEFT)) {
        isPressed = false;
        let v1 = createVector(presentPos[0], presentPos[1]);
        // let v2 = createVector(previousPos[0], previousPos[1]);
        let v2 = createVector(0, width);
        let angle = v1.angleBetween(v2);
        let speed = abs(presentPos[0]-previousPos[0]) + abs(presentPos[1]-previousPos[1]);
        console.log(angle*360);
        ball.addSpeed(speed/2, angle*360);
    }

    if (ball.velocity.x > 0) ball.velocity.x*=.95;
    if (ball.velocity.y < 9.8) ball.velocity.y+=.15;

    drawSprites();
}

