function rPixels(size, steps) {
  let rImg = createImage(size, size);
  rImg.loadPixels();

  for (let y = 0; y < rImg.height; y += 3) {
    for (let x = 0; x < rImg.width; x += 3) {
      const rC = color((y * steps) % 360, 360, 360);
      rImg.set(x, y, rC);
    }
  }
  rImg.updatePixels();
  noSmooth();
  image(rImg, 0, 0, width, height);

  if (size >= 1) {
    size *= 5;
    steps *= 5;
    rPixels(size, steps);
  }
}

