function poly(sides) {
  noFill();
  stroke(255);
  background(40);
  let steps;
  if (sides >= 3 && sides <= 4) {
    myTurtle.moveTo(90, 60);
    steps = sides + 0.7 * width;
  } else if (sides === 5) {
    myTurtle.moveTo(135, 60);
    steps = sides + 0.5 * width;
  } else if (sides === 6) {
    myTurtle.moveTo(135, 60);
    steps = sides + 0.45 * width;
  } else if (sides === 7) {
    myTurtle.moveTo(165, 60);
    steps = sides + 0.35 * width;
  } else if (sides === 8) {
    myTurtle.moveTo(180, 60);
    steps = sides + 0.3 * width;
  } else if (sides === 9) {
    myTurtle.moveTo(190, 60);
    steps = sides + 0.25 * width;
  } else if (sides === 10) {
    myTurtle.moveTo(185, 45);
    steps = sides + 0.25 * width;
  } else {
    myTurtle.moveTo(200, 45);
    steps = sides + 0.1 * width;
  }
  myTurtle.penDown();
  for (let i = 0; i < sides; i += 1) {
    const angle = 360 / sides;
    myTurtle.moveForward(steps);
    myTurtle.turnRight(angle);
  }
  console.log(`${sides}`);
}

