const storyGrammar = {
    "story": ["It was a dark and stormy night. #phrase1#. #phrase2#. #phrase3#, and #phrase4#. He was able to guide us back by our #endplace#"],
    "phrase1": ["we weren't allowed to go in the #place1#. it was #adjective# and #adjective# and smelled like a #smellNoun#"],
    "phrase2": ["the steps leading down into the #place2# were #adjective#. and we #verb1# towards it."],
    "phrase3": ["Suddenly, we heard howling of #animal#, and the lights went off"],
    "phrase4": ["Luckly, Our #pet#, #petName#, was able to lead us back out"],

    "place1": ["house", "old school", "abandoned structure", "cementry", "basement", "old hotel"],
    "adjective": ["cold", "dark", "daunting", "bizarre","crawly", "creepy", "chilling"],
    "smellNoun": ["dead fish", "cropse", "gas", "popcorn", "cotton candy", "lemon", "oranges", "candles"],
    "place2": ["basement","cave","room","ground", "backyard", "forest"],
    "verb1": ["crawled","walked","jumped","ground", "moved"],
    "animal": ["wolves", "bulldog bats", "Howler monkeys", "Elephants", "Lions"],
    "pet": ["James", "Gamja", "Trump", "sam", "dillon", "jake"],
    "petName": ["cat", "dog", "frog", "duck", "bird", "hedgehog"],
    "endplace": ["campfire", "hotels", "camping car", "classroom", "van", "car", "cabin"],
}


function main() {
    let grammar = tracery.createGrammar(storyGrammar);
    let story = grammar.flatten("#story#");

    const storyDiv = document.createElement('div');

    document.body.style.background = 'navy';
    storyDiv.style = "font-size: 25px; margin: 10%; line-height: 1.5; color: white";
    storyDiv.textContent = story;

    document.body.insertAdjacentElement("beforeend", storyDiv);
}


