// language paperscript
// require https://cdnjs.cloudflare.com/ajax/libs/paper.js/0.11.5/paper-full.min.js

var point = view.center;

var colors = [];
var cycles = 3;
for (var i = 0, l = 60; i < l; i++) {
	var brightness = 2 - (i / l) * 1.2;
	var hue = i/l * cycles * 50;
	var color = {
		hue: hue,
		saturation: 1,
		brightness: brightness
	};
	colors.push(color);
}

var radius = Math.max(view.size.width, view.size.height) * .75;

var path = new Path.Rectangle({
	rectangle: view.bounds,
	fillColor: {
		origin: point,
		destination: point + [radius, 0],
		gradient: {
			stops: colors,
			radial: true
		}
	}
});

var color = path.fillColor;
var gradient = color.gradient;
var mouseDown = false;
var mousePoint = view.center;

function onMouseDown(event) {
	mouseDown = true;
	mousePoint = event.point;
}

function onMouseDrag(event) {
	mousePoint = event.point;
}

function onMouseUp(event) {
	vector.length = 10;
	mouseDown = false;
}

var grow = false;
var vector = new Point(0);

function onFrame() {
	for (var i = 0, l = gradient.stops.length; i < l; i++)
		gradient.stops[i].color.hue -= .75;
	if (grow && vector.length > 20) {
		grow = false;
	} else if (!grow && vector.length < 10) {
		grow = false;
	}
	if (mouseDown) {
		point = point + (mousePoint - point) / 10;
	} else {
		vector.length += (grow ? 1 : -1);
		vector.angle += 5;
	}
	color.highlight = mouseDown ? point : point + vector;
}

function onResize(event) {
	point = view.center;
	path.bounds = view.bounds;
	var color = path.fillColor;
	color.origin = point;
	var radius = Math.max(view.size.width, view.size.height) * 0.75;
	color.destination = point + [radius, 0];
}

var amplitude_slider, freq_slider, speed_slider, change_shape;

var startX = 0;
var startY = 300;
var endX = 500;
var endY = 0;
var vel = 0;


function setup() {
  createCanvas(500, 300);
  rectMode(CENTER);
  colorMode(HSB);

  createP('Amplitude');
  amplitude_slider = createSlider(0, 100, 5);
  createP('Frequency');
  freq_slider = createSlider(0, 100, 5);
  createP('Speed');
  speed_slider = createSlider(0, 100, 20);
  createP('Transparency');
  alpha_slider = createSlider(0, 100, 50);
  change_shape = createCheckbox('Change to Rectangle', false);


}


function draw() {
  background(10);
  ellipseMode(CENTER);

  var amplitude = amplitude_slider.value() / 100;
  var freq = freq_slider.value();
  var speed = speed_slider.value() / 100;
  var alpha = alpha_slider.value();

  noiseDetail(1, 0.5);



  noStroke();

  // study this loop. do you understand how the line of ellipses is created?

  vel += speed;
  for (i = 0; i < 1; i += 0.02) {
    for (j = 0; j < 1; j += 0.02) {



      var a = map(alpha, 0, 100, 0, 255);

      fill(a * i, 100, 100);
      //fill(100, a);
      //stroke(100, a);
      var x = lerp(startX, endX, i);
      var y = lerp(startY, endY, j);


      // hint: drive offsetX and offsetY with noise() instead of random()
      var offsetX = noise(freq * i + vel) * amplitude * 100;
      //var offsetX = noise(freq * j + vel) * amplitude * 100;
      var offsetY = noise(freq * j + vel) * amplitude * 100;
      //var offsetY = noise(freq * i + vel) * amplitude * 100;



      if (change_shape.checked()) {
        rect(x + offsetX, y + offsetY, 10, 10);
      } else {
        ellipse(x + offsetX, y + offsetY, 10, 10);
      }


    }

  }

}

