    randomFill();
    randomSpot();
    let radiusSize = random(5, 60);
    let tubeSize = radiusSize / 2;
    donuts[i] = new Torus(
      radiusSize,
      tubeSize,
      xPos,
      yPos,
      xMotion,
      yMotion,
      redVal,
      greenVal,
      blueVal
    );
  }
}

function draw() {
  background(175);
  for (let i = 0; i < 10; i++) {
    noStroke();
    donuts[i].rotation();
    donuts[i].translation();
    donuts[i].display();
  }
}

class Torus {
  constructor(x, y, a, b, t, u, redVal, greenVal, blueVal) {
    this.x = x;
    this.y = y;
    this.a = a;
    this.b = b;
    this.t = t;
    this.u = u;
    this.redVal = redVal;
    this.greenVal = greenVal;
    this.blueVal = blueVal;
  }
  translation() {
    translate(this.a, this.b);
  }
  rotation() {
    rotateX(frameCount * this.t);
    rotateY(frameCount * this.u);
  }
  display() {
    fill(this.redVal, this.greenVal, this.blueVal);
    torus(this.x, this.y);
  }
}

function randomSpot() {
  xPos =
    (random(0, 100) + random(0, 100) + random(0, 100) + random(0, 100)) * 0.25;

  yPos =
    (random(0, 100) + random(0, 100) + random(0, 100) + random(0, 100)) * 0.25;

  return xPos, yPos;
}

function randomMotion() {
  xMotion = random(0.01, 0.05);
  yMotion = random(0.01, 0.05);
  //   console.log(xMotion, yMotion);
  return xMotion, yMotion;
}

function randomFill() {
  redVal = random(0, 255);
  greenVal = random(0, 255);
  blueVal = random(0, 255);
  //   console.log(redVal, greenVal, blueVal);
  return redVal, greenVal, blueVal;
}

