var freq_slider, size_slider, noise_slider;

function setup() {
  createCanvas(500, 500);

  createP('Frequency');
  freq_slider = createSlider(0, 10, 2, 0.1);

  createP('Density');
  size_slider = createSlider(1, 100, 50);

  createP('Noise Level');
  noise_slider = createSlider(0, 100, 20);

}

function draw() {
  background(0, 0, 0, 25);

  var frequency = freq_slider.value() / 100;
  var s = size_slider.value();
  var noiseMax = noise_slider.value();


  for (var x = 0; x < width; x += s) {
    for (var y = 0; y < height; y += s) {

      let xoff = noise(frameCount * frequency);
      let yoff = noise(frequency);
      noStroke();
      fill(x / 2, 50);

      let r = noise(frameCount * frequency) * noiseMax;

      rectMode(CENTER);
      rect(x, y, r * s, r);

    }
  }
}

