	myTurtle.penDown();
	drawBranch(100);
}


function drawBranch(length) {

	if (length < 15) {
		return;
	}

	// draw this branch
	myTurtle.moveForward(length);

	// left child
	myTurtle.pushState();
	myTurtle.turnLeft(90);
//myTurtle.turnLeft(120);
	drawBranch(length * 0.75);
	//drawBranch(length * random(.5, .9));

	myTurtle.popState();

	// right child
	myTurtle.pushState();
	myTurtle.turnRight(90);
myTurtle.turnRight(60);
	drawBranch(length * 0.75);
	//drawBranch(length * random(.5, .9));
	myTurtle.popState();

}

