  colorMode(HSB);
  frameRate(120);
  myTurtle = new Turtle();
}

function draw() {
  background(20);

  noFill();
  strokeWeight(1);

  // move to starting position (without drawing)
  const xStart = 110;
  const yStart = 75;
  myTurtle.penUp();
  myTurtle.moveTo(xStart, yStart);

  for (let i = 0; i < 13; i += 1) {
    colorPulse = frameCount % 360;
    stroke(colorPulse * i, 360, 360);
    myTurtle.penDown();
    for (let j = 0; j < 5; j += 1) {
      myTurtle.moveForward(250 - i * 20);
      myTurtle.turnRight(72);
    }
    myTurtle.penUp();
    myTurtle.moveTo(i * 10 + 120, i * 14 + 88);
  }

}

