  circleCheck.changed(circleDraw);
  squareCheck = createCheckbox('Draw Squares!', false);
  squareCheck.changed(squareDraw);
  noLoop();
}

function draw() {
  background(35);

  noStroke();
  ellipseMode(CENTER);
  rectMode(CENTER);

  if (circleCheck.checked() === true) {
    for (let i = 0; i < inp.value(); i += 1) {
      // these points are not scattered in the same way
      // how can you make the arrangement match the challenge?
      let x = (random(30, width) + random(30, width)) / 2;
      let y = (random(30, height) + random(30, height)) / 2;

      let xGaus = randomGaussian(200, 60);
      let yGaus = randomGaussian(200, 60);

