      // let diameter = random(8, 10);

      let hueNoise = noise(xGaus);
      let hue = map(hueNoise, 0, 1, 0, 360);

      let diameter;
      if (random() < 0.34) {
        diameter = 10;
      } else if (random() < 0.66) {
        diameter = 7;
      } else if (random() < 0.98) {
        diameter = 5;
      } else {
        diameter = 3;
        hue = 0;
      }
      fill(hue, 100, 100);

      ellipse(xGaus, yGaus, diameter, diameter);
    }
    true;
  } else {
    // inp.remove();
    redraw();
    false;
  }

  if (squareCheck.checked() === true) {
