function shape(center, radius, points) {
  var path = new Path();
  for (var i = 0; i < points; i++) {
    var move = new Point({
      length: radius * 0.81 + Math.random() * radius * 0.7,
      angle: (320 / points) * i
    });
    path.add(center + move);
  }
  return path;
}

