function drawHead() {

    myTurtle.penUp();
	myTurtle.moveTo(210, 310);
	myTurtle.turnTo(-45);
	myTurtle.penDown();

	//top of head
	for(var a = 0; a < 62; a++) {
        myTurtle.moveForward(1.5);
        myTurtle.turnRight(1.5);
    }

    //right ear
    myTurtle.turnTo(-60);

    for(var d = 0; d < 180; d++) {
        myTurtle.moveForward(.35);
        myTurtle.turnRight(.35);
    }

    myTurtle.turnTo(-240);

    for(var d = 0; d < 180; d++) {
        myTurtle.moveForward(.35);
        myTurtle.turnRight(.35);
    }

    //right side of face
    myTurtle.turnTo(90);

    for(var b = 0; b < 124; b++) {
        myTurtle.moveForward(.75);
        myTurtle.turnRight(.35);
    }

    myTurtle.turnTo(90);

    //nose
    for(var c = 0; c < 180; c++) {
        myTurtle.moveForward(.175);
        myTurtle.turnRight(1);
    }

    //left side of face
    myTurtle.turnTo(225);

    for(var d = 0; d < 124; d++) {
        myTurtle.moveForward(.75);
        myTurtle.turnRight(.375);
    }

    //left ear
    myTurtle.turnTo(240);

    for(var d = 0; d < 180; d++) {
        myTurtle.moveForward(.35);
        myTurtle.turnLeft(.35);
    }

    myTurtle.turnTo(60);

    for(var d = 0; d < 180; d++) {
        myTurtle.moveForward(.35);
        myTurtle.turnLeft(.35);
    }

}

function drawRightAntler(l) {

    if (l < 15) {
		return;
	}

	strokeWeight(l/11);

    var m = l*.65;

    //draw this antler
    for(var i = 0; i < l; i++) {
        myTurtle.moveForward(1.25);
        myTurtle.turnRight(l/85);
    }

 	myTurtle.pushState();
	myTurtle.turnLeft(l/1.25);
	drawRightAntler(m);

	myTurtle.popState();

	myTurtle.pushState();
	myTurtle.turnLeft(120);
	drawRightAntler(m);
	myTurtle.popState();

}

function drawLeftAntler(l) {

    if (l < 15) {
		return;
	}

	strokeWeight(l/11);

    var m = l*.65;

    //draw this antler
    for(var i = 0; i < l; i++) {
        myTurtle.moveForward(1.25);
        myTurtle.turnLeft(l/85);
    }

 	myTurtle.pushState();
	myTurtle.turnRight(l/1.25);
	drawLeftAntler(m);

	myTurtle.popState();

	myTurtle.pushState();
	myTurtle.turnRight(120);
	drawLeftAntler(m);
	myTurtle.popState();

}

