function alterImage(img) {
    img.loadPixels();

  for (let y = 0; y < img.height; y ++) {
    for (let x = 0; x < img.width; x ++) {
        colorMode(HSB);
        // setting the colors, randomizing some values
        let c1 = color(6, (random(30,90)), (random(10,75)));
        let c2 = color((random(20, 170)), 91, 80);
