var myTurtle;


function setup() {
	createCanvas(400, 400);
	noFill();
	stroke(255);

	background(0);
	noLoop();
	myTurtle = new Turtle();
}



function draw() {
    myTurtle.penUp();
	myTurtle.moveTo(150,300);
    myTurtle.turnLeft(90);
    myTurtle.penDown();
    for (let i=0; i<50; i++) {
      strokeWeight(3);
      myTurtle.moveForward(50-i);
      myTurtle.turnRight(30);
      if(i < 40 ) {
        drawPattern();
      }
    }    

    strokeWeight(1);
    moveToBody(); // body
    drawNeck(); // nectk

    for (let i=0; i<20; i++) { // face
      myTurtle.moveForward(5);
      myTurtle.turnLeft(10);
      if(i%11 == 0 ) { // eyes
        drawEyes();
      }
    }

    myTurtle.turnRight(15);

  for (let i=0; i<50; i++) { // body bottom
    myTurtle.moveForward(10);
    myTurtle.turnLeft(1+(i/10));
  }

  myTurtle.turnLeft(150);
  for(let i=0; i<13; i++){
    myTurtle.moveForward(13-i);
    myTurtle.turnRight(5);
  }
}


function drawPattern() {
    myTurtle.pushState();
	for (i = 0; i < 15; i++) {
		myTurtle.moveForward(2);
		myTurtle.turnRight(5+i);
	}
	myTurtle.popState();
}

function moveToBody() {
   myTurtle.penUp(); // move to Body;
    myTurtle.moveTo(150,300);
    myTurtle.penDown();
    myTurtle.turnLeft(90);

}

function drawNeck() {
  for (let i=0; i<15; i++ ){ // neck
      myTurtle.moveForward(10);
      myTurtle.turnRight(1);
    }
}

function drawEyes() {
  myTurtle.pushState();
  myTurtle.turnRight(30);
  for (let i=0; i<50; i++) {
    myTurtle.moveForward(5);  // eyes
    myTurtle.turnRight(5+i);
  }
  myTurtle.popState();
}

