      line(random(20, 100),300, random(400), 300);
          stroke(random(80,180));
      line(random(400, 700),600, random(200,400), 600);
          stroke(random(80,180));
      line(random(600),60, random(300,600), 60);
          stroke(random(80,180));
      line(600, random(600), 600, random(100,200));


  for (var x = 0; x <= width; x += 30) {
    for (var y = 0; y <= height; y += 30) {
      strokeWeight(1);
      stroke(0);
      noFill();
      ellipse(x, y, 4, 4);

      fill(0);
      ellipse(x * 3, y * 3, 5, 5);

    }

  }





  while (circles.length < 100) {

    //create a circle object & push them into array
    var circle = {
      x: random(width),
      y: random(height),
      r: random(3, 15),
    }

    var overlapping = false;

    //check circle positions before adding to array for no overlap
    for (var j = 0; j < circles.length; j++) {
      var other = circles[j];
      var d = dist(circle.x, circle.y, other.x, other.y);
      if (d < circle.r + other.r) {
        overlapping = true;
        break;
      }
    }

    if (!overlapping) {
      circles.push(circle);
    }
  }

  for (var i = 0; i < circles.length; i++) {
    fill(random(50, 200), 100);
    noStroke();
    ellipse(circles[i].x, circles[i].y, circles[i].r * 2, circles[i].r * 2);
  }


