function colorGrabBlue(minVal) {
  for (var y = 0; y < chromPrint.height; y++) {
      for (var x = 0; x < chromPrint.width; x++) {
        in_color = chromPrint.get(x, y);
        if (blue(in_color) > minVal && blue(in_color) > red(in_color) && blue(in_color) > green(in_color)) {
          colorDrag = in_color;
        }
        chromPrint.set(x, y, colorDrag);
        chromPrint.updatePixels();
      }
}
}

function colorGrabRed(minVal) {
  for (var y = 0; y < chromPrint.height; y++) {
      for (var x = 0; x < chromPrint.width; x++) {
        in_color = chromPrint.get(x, y);
        if (red(in_color) > minVal && red(in_color) > green(in_color) && red(in_color) > blue(in_color)) {
          colorDrag = in_color;
        }
        chromPrint.set(x, y, colorDrag);
        chromPrint.updatePixels();
      }
  }
}

function colorGrabGreen(minVal) {
  for (var y = 0; y < chromPrint.height; y++) {
      for (var x = 0; x < chromPrint.width; x++) {
        in_color = chromPrint.get(x, y);
        if (green(in_color) > minVal && green(in_color) > blue(in_color) && green(in_color) > red(in_color)) {
          colorDrag = in_color;
        }
        chromPrint.set(x, y, colorDrag);
        chromPrint.updatePixels();
      }
  }
}

