let arr = [];
let rows = 0;

function preload() {
  mySound = loadSound('Tick-Tock.wav');

  fft = new p5.FFT(0.8, 128);
  fft.setInput(mySound);

}

function setup() {
  createCanvas(400, 200);
  background(50);
  const startButton = createButton('play');
  startButton.mousePressed(start);
  noStroke();
  mySound.play(0, 1, 1);
}

function draw() {
  const wf = fft.analyze();
  for (let i = 0; i < 128; i++) {
    if (wf[i] != 0) {
      arr.push(wf[i]);
    }
  }
}

function start() {
  mySound.play(0, 1, 1);
  for (let j = 1; j < arr.length; j++) {
    let val = map(arr[j], 0, 100, 0, 255);
    fill(val, val%127 + 51, val%58 - 67);
    rect(j % width*10, rows, 10, 10);
    if (j % width == 0) {
      rows+=10;
    }
    if (rows > height) {
      rows = 0;
    }
  }
}

