let ball, hoop, limit, boxes, goal;
let presentPos, previousPos;
let isPressed;
let score = 0;

function setup() {
    createCanvas(600, 600);

    ball = createSprite(width/2, 400, 20, 20);
    limit = createSprite(0, height/2,50,10);
    ball.setCollider('circle', 0, 0, 10, 10);

    presentPos = [ball.position.x, ball.position.y];

    ball.onMousePressed = function() {
        isPressed = true;
    }

    hoop = new Group();
    let hoop1 = createSprite(width-100, 200, 20, 100);
    hoop1.immovable = true;
    hoop.add(hoop1);
    let hoop2 = createSprite(width-20, 200, 20, 100);
    hoop2.immovable = true;
    hoop.add(hoop2);
    let hoop3 = createSprite(width-60, 250, 100, 20);
    hoop3.immovable = true;
    hoop.add(hoop3);

    goal = createSprite(width-60, 237, 50, 5);
    goal.immovable = true;

    boxes = new Group();
    let box = createSprite(5, height * 0.5, 10, height);
    box.immovable = true;
    boxes.add(box);
    let box2 = createSprite(width - 5, height * 0.5, 10, height);
    box2.immovable = true;
    boxes.add(box2);
    let box3 = createSprite(width * 0.5, 5, width, 10);
    box3.immovable = true;
    boxes.add(box3);
    let box4 = createSprite(width * 0.5, height - 5, width, 10);
    box4.immovable = true;
    boxes.add(box4);
}

function reduceVelocity() {
    ball.velocity.y*=.5;
}

function returnToFloor() {
    score++;
    ball.position.x = width/2;
    ball.position.y = height - 50;
    ball.velocity.x = 0;
    ball.velocity.y = 1;
}

function draw() {
    background(50, 50, 80);
    previousPos = presentPos;
    presentPos = [ball.position.x, ball.position.y];
    ball.bounce(boxes, reduceVelocity);
    ball.bounce(hoop, reduceVelocity);
    ball.bounce(goal, returnToFloor);

    if (isPressed) {
        ball.position.x = mouseX;
        ball.position.y = mouseY;
    }
    if (mouseWentUp(RIGHT) || mouseWentUp(LEFT)) {
        isPressed = false;
        ball.velocity.x = presentPos[0] - previousPos[0];
        ball.velocity.y = presentPos[1] - previousPos[1];
    }
    if (isPressed && ball.position.y < limit.position.y) {
        isPressed = false;
        ball.velocity.y = -1;
    }

    if (ball.velocity.x > 0) ball.velocity.x*=.975;
    if (ball.velocity.y < 9.8) ball.velocity.y+=.15;

    drawSprites();

    fill(255);
    stroke(0);
    strokeWeight(6);
    textSize(36);
    text(score, 50, 50);

    //put ball back if it leaves the canvas
    if (ball.position.x > width || ball.position.x < 0 ||
    ball.position.y > height || ball.position.y < 0) {
        ball.position.x = width/2;
        ball.position.y = height - 50;
        ball.velocity.x = 0;
        ball.velocity.y = 1;
    }
}

