function randomStops() {
  var rR1 = Math.random();
  var rG1 = Math.random();
  var rB1 = Math.random();
  var col = new Color(rR1, rG1, rB1);
  return col;
}

function randomPoint() {
  var rX = Math.random() * width;
  var rY = Math.random() * height;
  return [rX, rY];
}

function randomSize(x, y) {
  var rSize1 = Math.random() * x;
  var rSize2 = Math.random() * y;
  return [rSize1, rSize2];
}

function randRad(r) {
  var rRad = Math.random() * r;
  return rRad;
}

var orig = [0, 0];
var dest = [width, height];

for (var i = 0; i < 20; i += 1) {
  if (Math.random() > 0.5) {
    var rect = new Path.Rectangle({
      point: randomPoint(),
      size: randomSize(200, 200),
    });
    rect.fillColor = {
      gradient: {
        stops: [randomStops(), randomStops(), randomStops()],
      },
      origin: rect.bounds.topLeft,
      destination: rect.bounds.bottomRight,
    };
  } else {
    var circle = new Path.Circle({
      center: randomPoint(),
      radius: randRad(100),
    });
    circle.fillColor = {
      gradient: {
        stops: [randomStops(), randomStops(), randomStops()],
        radial: true,
      },
      origin: circle.position,
      destination: circle.bounds.leftCenter,
    };
  }
}

