// find the RGBA values of the pixel at x, y in the img.pixels array
// see: http://p5js.org/reference/#/p5/pixels[]
// we don't need to worry about screen pixel density here, because we are not reading from the screen

function getQuick(img, x, y) {

  var i = (y * img.width + x) * 4;
  return [
    matisse.pixels[i],
    matisse.pixels[i + 1],
    matisse.pixels[i + 2],
    matisse.pixels[i + 3],
  ];
}

var testImage;

function preload() {
    testImage = loadImage("/rab.jpg");
    noLoop();
}

function setup() {
    // create a place to draw
    createCanvas(500, 500);
}


function draw() {
    // clear the background
    background(255);
    var start, end;
    var testImageCopy = createImage(300, 500);

    noSmooth();

    // draw original image
    image(testImage, 0, 0);

    // invert and draw
    testImageCopy.copy(testImage, 0, 0, 10, 10, 0, 0, 10, 10);
    start = millis();
    invertQuick(testImageCopy);
    end = millis();
    console.log("invertQuick: ", end - start);
    image(testImageCopy,0, 200);


    noLoop();
}


function invertQuick(img) {
    // load up the pixel[] array so we can read colors out of it
    img.loadPixels();


    for (y = 0; y < img.height; y++) {// noprotect
        for (x = 0; x < img.width; x++) {


            var c = getQuick(img, x, y);

            c = [
                255-c[0],
                c[1]-255,
                255-c[2],
                c[3],
            ];

          if(x*random(7)>y){
          img.set(x, y, c);
          }else{

           img.set(x, y, color(255,255,255));
          }

        }
    }

    img.updatePixels();
}



