function serpTri(x, y, length, rHue1, rHue1, rHue3, rHue4, rHue5) {
  const x1 = x;
  const y1 = y;
  const midpoint = length / 2;
  const x2 = midpoint + x1;
  const halfLeng = 0.5 * length;
  const square = sqrt(sq(length) - sq(halfLeng));
  const y2 = y - square;
  const x3 = length + x;
  const y3 = y1;
  fill(rHue1);
  triangle(x1, y1, x2, y2, x3, y3);
  const x4 = length / 4 + x1;
  const y4 = (y1 - y2) / 2 + y2;
  const x5 = length / 2 + x1;
  const y5 = y1;
  const x6 = length * 0.75 + x1;
  const y6 = y4;
  fill(rHue2, 255, 255);
  triangle(x4, y4, x5, y5, x6, y6);
  const x7 = length * (3 / 8) + x1;
  const y7 = (y1 - y2) / 4 + y2;
  const x8 = x5;
  const y8 = y4;
  const x9 = length * (5 / 8) + x1;
  const y9 = y7;
  fill(rHue3, 255, 255);
  triangle(x7, y7, x8, y8, x9, y9);
  const x10 = length / 8 + x1;
  const y10 = (y1 - y2) * (3 / 4) + y2;
  const x11 = length / 4 + x1;
  const y11 = y1;
  const x12 = length * (3 / 8) + x1;
  const y12 = y10;
  fill(rHue4, 255, 255);
  triangle(x10, y10, x11, y11, x12, y12);
  const x13 = length * (5 / 8) + x1;
  const y13 = y10;
  const x14 = length * (3 / 4) + x1;
  const y14 = y1;
  const x15 = length * (7 / 8) + x1;
  const y15 = y10;
  fill(rHue5, 255, 255);
  triangle(x13, y13, x14, y14, x15, y15);
  //   console.log(x12);
}

