let yoff = 0.0;
let rains = [];

function setup() {
  createCanvas(400, 400);
  for (let i = 0; i < 100; i++) {
    rains.push(new Rain());
  }
}

function draw() {
  let randomNum = min(random(0, 1), random(0, 1), random(0, 1), random(0, 1), random(0, 1));
  if (randomNum < 0.5) {
    frameRate(60);
    background(0);
    stroke(255);
    fill(255);
  } else {
    frameRate(10);
    background(255);
    stroke(0);
    fill(0);
  }
  for (let i = 0; i < rains.length; i++) {
    rains[i].update();
    rains[i].display();
  }
  // begin the bottom water shape
  beginShape();
  let xoff = yoff;
  for (let x = 0; x <= width; x += 10) {
    let y = map(noise(xoff, yoff), 0, 1, 280, 310);
    vertex(x, y);
    xoff += random(0, 0.02);
  }
  yoff += random(0, 0.05);
  vertex(width, height);
  vertex(0, height);
  endShape(CLOSE);

  drawHouse(width/2+100, 300);
}

class Rain {
  constructor() {
    this.x = random(width);
    this.y = random(height);
    this.l = random(10, 50);
  }

  update() {
    this.y += random(0, 5);
    if (this.y > height) {
      this.y = 0;
    }
  }

  display() {
    line(this.x, this.y, this.x, this.y + this.l);
  }
}

function drawHouse(x,y) {
  rect(x,y-50,50,50);
  triangle(x,y-50,x+25,y-70,x+50,y-50);
  fill(0);
  rect(x+10,y-40,10,10);
  rect(x+30,y-40,10,10);
}

