class gobbler {
  constructor() {
   this.x = dice(4,width);
   this.y = dice(4,height);
    let r= random();    
    this.d=r*10+10
    this.velocity=p5.Vector.random2D();
  }
  draw(){
    fill(this.d*10, 255, 255)
    ellipse(this.x,this.y,this.d,this.d)
  }
  update() {
    this.x+=this.velocity.x
    this.y+=this.velocity.y
    let radius=this.d/2
    if (this.x-radius<=0 || this.x+radius>=width) {
      this.velocity.x=-this.velocity.x
    }
    if (this.y-radius<=0 || this.y+radius>=height) {
      this.velocity.y=-this.velocity.y
    }
    // this.x%=width
    // this.y%=height
    for (let gobble of gobblers) {
      if (this.d>gobble.d && dist(gobble.x,gobble.y,this.x,this.y)<radius) {

          gobblers.splice(gobblers.indexOf(gobble),1)
          this.d+=gobble.d/8
          this.velocity=p5.Vector.random2D();
      }
    }
  }
}

