}

function draw() {


  translate(xPos, yPos);

  var radius = 200;

  beginShape();
  let xoff = 0.1
  for (var a = 0; a < TWO_PI; a += 0.04) {
    let offset = map(noise(xoff), 0, 1, 0, 100);
    let r = radius + offset;
    let x = r * cos(a);
    let y = r * sin(a);

    fill(53, 227, 18);
    noStroke();
    smooth();

    vertex(x,y);

    xoff += 0.2; 
    //ellipse(x, y, 4, 4);
  }
