function shuffle(a) {
    var j, x, i;
    for (i = a.length - 1; i > 0; i--) {
        j = Math.floor(Math.random() * (i + 1));
        x = a[i];
        a[i] = a[j];
        a[j] = x;
    }
    return a;
}

for(var a=0;a<=360;a+=plus){
    for(var r=75;r<=500;r+=Math.random()*50){
        for(var adj=5;adj<=10;adj+=0.5){
