//https://thecodingtrain.com/CodingChallenges/036-blobby.html
float increment = 0.02;
float yof = 0.0;

void setup() {
  size(640, 360);
}

void draw() {

  loadPixels();

  float xoff = 0.0; 
  float detail = .6;
  noiseDetail(8, detail);


  for (int x = 0; x < width; x++) {
    xoff += increment;   
    float yoff = 0.0;   
    for (int y = 0; y < height; y++) {
      yoff += increment; 

      float bright = random(0,255);

      pixels[x+y*width] = color(191,bright,3);
    }
  }

  updatePixels();
  float radius = 10;
pushMatrix();
noStroke();
fill(75,98,99);
translate(mouseX,mouseY);
  beginShape();
  float xof = 10;
  for (float a = 0; a < TWO_PI; a += 0.1) {
    float offset = map(noise(xof, yof), 0, 1, -200, 25);
    float r = radius + offset;
    float x = r * cos(a);
    float y = r * sin(a);
    vertex(x, y);
    xof += 0.1;
    ellipse(x, y, 10, 10);
  }
  endShape();

  yof += 0.01;
  popMatrix();
}

