let xspacing = 16;
let w; 
let theta = 0.5; 
let amplitude = 100.0; 
let period = 500.0; 
let dx; 
let yvalues; 
let xOff = 0.01;
let xOff2 = 0.001;
let noiseVar2;
function setup() {
  createCanvas(750, 750);
  w = width + 16;
  dx = (TWO_PI / period) * xspacing;
  yvalues = new Array(floor(w / xspacing));
}

function draw() {
  background(0,10);
  noiseVar = noise(xOff) * 100;
  noiseVar2 = noise(xOff2) * 50;
  xOff += 0.01;
  xOff2 += 0.001;
  calcWave();
  renderWave();
  period = noise(xOff2) * 10;
  noiseDetail(2,2.0);
  xspacing = noise(xOff) * 100;
}

function calcWave() {

  theta += 0.02;

  let x = theta;
  for (let i = 0; i < yvalues.length; i++) {
    yvalues[i] = sin(x) * amplitude + noiseVar;
    x += dx + noise(xOff2);
  }
}

function renderWave() {
  noStroke();
  fill(255);
  for (let x = 0; x < yvalues.length; x++) {
    ellipse(x * xspacing, height / 1.5 + yvalues[x], noiseVar, noiseVar);
  }

  for (let z = 0; z < yvalues.length; z++) {
    ellipse(z * xspacing, 200 / 2 + yvalues[z], noiseVar, noiseVar);
  }

}

