let baseballypos = -20;
let textfill = 0;
let finished = false;
let theta;
let batAngle;
let goingForward = true;

function setup() {
    createCanvas(350, 350);
    frameRate(3);
    textAlign(CENTER);
    textSize(24);
}

function draw() {
    background(0);

    fill(textfill);
    if (textfill === 0) {
        textfill = 255;
    } else {
        textfill = 0;
    }

    if (!finished) {

        text("SPACE TO HIT THE BALL", width/2, height-20);

        //baseball
        if (goingForward) {
            fill(255);
        } else {
            fill(0);
            noStroke();
        }
        ellipse(width/2, baseballypos, 20);
        baseballypos = map(sin(theta), 1, -1, 0, height);
        if (baseballypos >= height-10) {
            goingForward = false;
        } else if (baseballypos <= 10) {
            goingForward = true;
        }

        //win square
        noFill();
        stroke(127);
        rect(width/2 - 30, height/2 - 60, 80, 80);

        theta = map(frameCount, 30, 60, 1, 2 * PI);
        batAngle = sin(theta) * .5;
        baseballbat(0, height/2, 3, batAngle + 190);

    } else {
        textSize(36);
        text("HOME RUN\n\nHIT SPACE TO TRY\nAGAIN", width/2, (height/2)-36);
    }
}

function keyPressed() {
    if (key == ' ') {
        if (finished === true) {
            finished = false;
        //if the ball is within the win rectangle, get a home run
        } else if (baseballypos > height/2 - 60 &&
        baseballypos < height/2 + 20 && goingForward) {
            finished = true;
        }
    }
}

function baseballbat(x, y, size, pendulumAngle) {
    push();
    noStroke();
    translate(x, y);
    scale(size);

    // bat
    push();
    rotate(pendulumAngle);
    fill(150, 100, 100);
    rect(-3, -90, 6, 80);
    pop();
}

