  setGradient(0, 10, 500, 500, c1, c2);
}

function setGradient(x, y, w, h, color1, color2) {
  noFill();
  for (let i = x; i <= w; i++) {
    let inter = map(i, 0, w, 0, 1);
    let c = lerpColor(color1, color2, inter);
    stroke(c);
    line(i, y, i, y + h);
  }
}

